import React, { useState } from "react";
import Heatmap from "react-calendar-heatmap";
import "react-calendar-heatmap/dist/styles.css";
//import "./styles.css";

const SocialMediaProfile = () => {
  const [loggedInDays, setLoggedInDays] = useState([]);

  const handleLogin = () => {
    const newDay = new Date().toISOString().slice(0, 10);
    setLoggedInDays([...loggedInDays, newDay]);
  };

  return (
    <div className="container">
      <div className="profile">
        <img className="avatar" src="https://via.placeholder.com/150" alt="User Avatar" />
        <div className="user-info">
          <h2 className="username">John Doe</h2>
          <p className="bio">Web developer, coffee enthusiast, and avid reader.</p>
          <ul className="stats">
            <li>
              <strong>100</strong>
              followers
            </li>
            <li>
              <strong>50</strong>
              following
            </li>
            <li>
              <strong>25</strong>
              posts
            </li>
          </ul>
        </div>
      </div>
      <div className="about-me">
        <h3 className="section-title">About Me</h3>
        <p>
          Hi there! My name is John Doe and I'm a web developer based in New York City. I love building websites and
          applications that make people's lives easier. When I'm not coding, you can usually find me reading a book or
          trying out new coffee shops around the city.
        </p>
      </div>
      <div className="heatmap-container">
        <h3 className="section-title">Login Heatmap</h3>
        <Heatmap
          startDate={new Date("2022-01-01")}
          endDate={new Date("2022-12-31")}
          values={loggedInDays.map((day) => ({ date: new Date(day), count: 1 }))}
          showWeekdayLabels={true}
          classForValue={(value) => {
            if (!value) {
              return "color-empty";
            }
            return `color-github-${value.count}`;
          }}
          tooltipDataAttrs={(value) => {
            return {
              "data-tip": `${value.date.toDateString()} - ${value.count} logins`,
            };
          }}
        />
        <button onClick={handleLogin}>Log In</button>
      </div>
    </div>
  );
};

export default SocialMediaProfile;
