import React from 'react';
import { useState } from 'react';
import CalendarHeatmap from 'react-calendar-heatmap';
import 'react-calendar-heatmap/dist/styles.css';
import Navbar from './Navbar';
import FriendsList from './Friendlist';
import firebase from 'firebase';
import { useAuth } from '../contexts/AuthContext.js';
import { useEffect } from 'react';
import app from '../firebase';
function Profile() {
  const [searchText, setSearchText] = useState('');
  const [text2, setText2] = useState('');
  const [aboutMe, setAboutMe] = useState([]);
  const { currentUser } = useAuth();
  const [lastLogin, setLastLogin] = useState(null);

  // Simulating a login event
  const handleSearchChange = (event) => {
    setSearchText(event.target.value);
  };
  const handleSearchChange1 = (event) => {
    setText2(event.target.value);
  };
  const handleLogin = () => {
    const now = new Date();
    setLastLogin(now);
  };
  function Form() {
    return (
      <div>
      <form onSubmit={handleAddAboutMe(text2,searchText)}>
        <input type="text-box" placeholder="About Me" value={searchText} onChange={handleSearchChange} />
        <input type="text" placeholder="Interests" value={text2} onChange={handleSearchChange} />
        <button type="submit">Submit</button>
      </form>
      </div>
    );
  }
  useEffect(() => {
    const results = [];
   // const currentUser = firebase.auth().currentUser;
    const userId = app.auth().currentUser.uid;//const LoginDate = new Date();
    const db = app.firestore()
    //const currentUserRef = db.collection('users').doc(currentUser.uid);
    const AboutRef = db.collection('users').doc(userId);
    //const friendsRef = firebase.firestore().collection("friends");

    // Query all friends for the logged-in user
    const query = AboutRef
    query.get().then((querySnapshot) => {
        results.push(querySnapshot)
        // querySnapshot.forEach((doc) => {
        //   results.push({ id: doc.id, ...doc.data() });
        //   console.log(results)
        // });

        setAboutMe(results);
      })
      .catch((error) => {
        console.error('Error searching for users:', error);
      });


  }, []);
  const handleAddAboutMe = (text2,text) => {
    const db = firebase.firestore();
    const currentUserRef = db.collection('users').doc(currentUser.uid);
    currentUserRef.set({
        email: currentUser.email,
        loginDate: new Date(),
        Aboutme: text,
        Interests: text2
    })
      .then(() => {
        console.log('About me  added successfully!');
      })
      .catch((error) => {
        console.error('Error adding About me:', error);
      });
  };
  // Generate random heatmap data
  const generateHeatmapData = () => {
    const today = new Date();
    const yearAgo = new Date(today.getFullYear() - 1, today.getMonth(), today.getDate());

    const data = [];

    for (let date = yearAgo; date <= today; date.setDate(date.getDate() + 1)) {
      const count = Math.floor(Math.random() * 4);
      data.push({
        date: new Date(date.getTime()),
        count,
      });
    }

    return data;
  };
  const [showForm, setShowForm] = useState(false);

  const toggleForm = () => {
    setShowForm(!showForm);
  };
  const heatmapData = generateHeatmapData();

  // Calculate number of empty days
  const emptyDays = heatmapData.filter((data) => data.count === 0).length;

  return (
    <div className="container">
      <Navbar/>
      <div className="profile">
        <img
          className="avatar"
          src="https://avatars.githubusercontent.com/u/14101776?v=4"
          alt="avatar"
        />
        <div>
          <h1 className="username">John Doe</h1>
        </div>

      </div>

      <div className="about-me">
        <h2 className="section-title">About Me</h2>
        <p>
        <div>
      <button onClick={toggleForm}>Open Form</button>
      {showForm && <Form />}
    </div>
          {aboutMe.Aboutme}
        </p>
      </div>

      <div className="heatmap-container">
        <h2 className="section-title">Daily Login Heatmap</h2>
        {lastLogin && (
          <div className="heatmap-info">
            <p>Last login: {lastLogin.toDateString()}</p>
            <p>Empty days: {emptyDays}</p>
          </div>
        )}
        <CalendarHeatmap
          startDate={new Date('2022-04-01')}
          endDate={new Date('2023-04-01')}
          values={heatmapData}
          showWeekdayLabels={true}
          weekdayLabels={['Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat']}
          onClick={handleLogin}
        />
      </div>
      <FriendsList/>
    </div>
  );
}

export default Profile;
